mapper.select:
select a.*,b.himg from house a join house_img b on a.id=b.hid
join (select * from code where code_type=7) c on a.house_layout=c.code_number


        and id like "%"#{id}"%"


        and a.house_rent  >=  #{rentStart}


        and a.house_rent  <=  #{rentOver}


        and a.house_layout =#{houseLable}


        and a.house_urban =#{houseUrban}


        and a.house_street =#{houseStreet}

 limit ${start},${pageSize}

select a.*,b.himg from house a join  house_imgon b on a.id=b.hid
join (select * from code where code_type=7) c on a.house_layout=c.code_number


        and id like "%"#{id}"%"


        and a.house_rent  >=  #{rentStart}


        and a.house_rent  <=  #{rentOver}


        and a.house_layout =#{houseLable}


        and a.house_urban =#{houseUrban}


        and a.house_street =#{houseStreet}
